#query and view specific elements with inline fragment:
query getAllElements($workspaceId: String!){
    elements(workspaceId: $workspaceId ) {
        type: __typename        
        id pinned zIndex traits
        transform {
            x
            y
            scaleX
            scaleY
        }
        comments{
            actorId
            actorType
            commenterName
            createdAt
            id
            parentId
            text
            traits
            blocks{
                content
            }
        }

        ... on Image{
            height
            width
            ingestionState
            asset{
                imageFormat
                url
            }
        }

        ... on Video{
            width
            height
        }

        ... on Shape{
            kind
            style{
                height
                width
                fillColor{
                    r
                    g
                    b
                    a
                }
            }
        }

    ... on Text{
        text
        style{
            textTransform
            fontSize
            fontFamily
            verticalAlign
            color{
                r g b a
            }
            backgroundColor{
                r g b a
            }
            width
            height
        }

        blocks{
            align
            ... on TextList{
                align
                kind
                items{
                    align
                    indent
                    content
                }
            }

            ... on TextBlock{
                align
                content
            }
        }
    }

    }
}

#get elements from a workspace for a given type
#types are defined in graphQL schema, current types are: 
#Text, LegacyNote, Image, Document, Grid, Browser, Video, Stroke
#Shape, Line, LinkedDocument, Canvas, Selection, Window, Whiteboard
query getElements($workspaceId: String!){
        elements(workspaceId: $workspaceId, type:[Shape, Image], flatten:false) {
            type:__typename
            id
            transform{
                x
                y
                scaleX
                scaleY
            }
            comments{
                actorId
                actorType
                commenterName
                createdAt
                id
                parentId
                text
                traits
                blocks{
                    content
                }
            }

            ... on Stroke{
                style{
                    strokeWidth
                    brushType
                }
                points
            }

            ... on Shape {
                traits
                text
                textStyle{
                    fontSize
                    verticalAlign
                    fontFamily
                }
                style{
                    width
                    height
                }
            }
            ... on Browser {
                style{
                    width
                    height
                }
            }
            ... on Image{
                width
                height
                ingestionState
                asset{
                    imageFormat
                    url
                }

            }

            ... on Video{
                width
                height
                filename
                sourceUrl
            }
            ... on LegacyNote{
                width
                height
            }
            ... on Document{
                width
                height
            }       

            ... on Grid{
                rowCount
                columnCount
                style{
                    horizontalCellMargin
                    verticalCellMargin
                    cellWidth
                    cellHeight
                }
            }

            ... on Canvas {
                style{
                    width
                    height
                }
            }

            ... on Text {
                style{
                    width
                    height
                }
            }

            ... on Selection{
                id
                selected{
                    id
                    selectedType: __typename
                }
            }

            ... on Line{
                style{
                    strokeWidth
                }

                start{
                    ... on AbsoluteLinePoint{
                        x
                        y
                    }
                }

                end{
                    ... on AbsoluteLinePoint{
                        x
                        y
                    }
                }
            }
        }
    }

query getShapeQuery($workspaceId: String!){
    elements(workspaceId: $workspaceId, type:[Shape] ) {
        type: __typename        
        id pinned zIndex traits
        transform { 
            x 
            y 
            scaleX 
            scaleY 
        }

        #In the query below, note the use of the "...myShape" fragment
        #The fragment is defined after this query
        ... on Shape{ ...myShape}
    }
}

#use fragments to help make queries more modular and to create reausable objects:
fragment myShape on Shape{
            kind
            text
            aspectRatioFixed
            style{
                __typename
                width
                height
                fillColor{ r g b a}
                ... on StickyShapeStyle{
                    width
                    height
                    fillColor{
                        ...allColor
                    }
                }
                ... on RegularShapeStyle{
                    width
                    height
                    strokeWidth
                    strokeColor{
                        #Note how this fragment uses the "...allColor" fragment (defined at the bottom of this request)
                        ...allColor
                    }
                    fillColor{
                        ...allColor
                    }
                }
            }
        }

fragment allColor on Color { r g b a }

query CanvasElements($workspaceUid: String!, $canvasId: String!) {
    elements: elements(workspaceId: $workspaceUid, canvasId: $canvasId) {
        __typename
        id
        ... on Element { transform { ...allTransform } }
        ... on Text { zIndex pinned text style { width height color { ...allColor} 
        backgroundColor { ...allColor } } } 
        ... on Image { zIndex width height } 
        ... on Stroke { zIndex points style { strokeWidth color { ...allColor } brushType } }
        ... on Document { width height title asset { documentFormat } }
        ... on LinkedDocument { style { width height } }
        ... on Shape { kind shapeText:text zIndex style {  width height fillColor{ ...allColor } } }  
        ... on Browser {style { width height } } 
        ... on Window {style { width height } }
        ... on Video {filename width height }
        ... on Whiteboard {style { width height } }
        ... on Grid {transform { ...allTransform } rowCount columnCount style { cellWidth cellHeight } }
        ... on Line { style { strokeWidth strokeColor { ...allColor } } start { ... on AbsoluteLinePoint { x y } } end { ... on AbsoluteLinePoint { x y } } } } }

    fragment allTransform on Transform { x y scaleX scaleY }
    fragment allColor on Color { r g b a }

