mutation myCreateCanvas($ws: String! $canvasName: String! $x: Float! $y: Float!, $width:Float!, $height:Float!) {
        createCanvas(
            workspaceId: $ws
            input: {
                name: $canvasName
                transform: {
                    x:$x 
                    y:$y
                }
                style:{
                    width:$width
                    height:$height
                    #Valid borderColor are as follows:
                    #blue "r": 153, "g": 198, "b": 255, "a": 1
                    #green "r": 130, "g": 222, "b": 198, "a": 1
                    #yellow "r": 254, "g": 240, "b": 159, "a": 1
                    #red "r": 255, "g": 161, "b": 161, "a": 1
                    #pink "r": 255, "g": 158, "b": 230, "a": 1
                    #purple "r": 221, "g": 173, "b": 255, "a": 1
                    borderColor:{
                        r:153
                        g:198
                        b:255
                        a:1
                    }
                }
            }
        )
        {
            #return from Canvas:
            id
            zIndex
            name
            transform {x y scaleX scaleY}
            pinned
            style{width height}
        }
      }

