#create shape with json-ld traits data
mutation myCreateShape($ws: String! $x:Float! $y:Float!, $width:Float!, $height:Float!, $g:Int, $strokeWidth:Float!, $fontSize:FontSize!, $shapeText:String, ) {
      shape1:createShape(
          workspaceId: $ws
          input: {
            pinned:false

            #store JSON-LD metadata that can be accessed with query on the element:
            traits:{context: "http://schema.org/", content: {testBox: "1"}}

            mirrorX: false
            mirrorY: false
            text:$shapeText
            style:{
                regularShape:{
                    kind: Rectangle
                    width:$width height:$height strokeWidth:$strokeWidth
                    fillColor: {r:255 g:161 b:161 a:1}
                    strokeColor:{r:255 g:$g b:0 a:1} 
                }
            }
            textStyle:{
                #fontSize = "auto" or size int
                fontSize:$fontSize
                #verticalAlign = top, center, bottom
                verticalAlign:center
            }
          transform: {x:$x y:$y}})
    #return values from Shape type:
    {id pinned kind mirrorX mirrorY traits transform{x y scaleX scaleY} style{ height width fillColor{r g b a}} }
}

